(function() {
  var SimpleTool, Tools, TouchCanvas, worldPosition;

  TouchCanvas = require("touch-canvas");

  module.exports = function(state) {
    var canvas;
    canvas = TouchCanvas({
      width: 512,
      height: 288
    });
    canvas.on("touch", function(e) {
      return Tools[state.activeTool].touch(e, state);
    });
    canvas.on("move", function(e) {
      return Tools[state.activeTool].move(e, state);
    });
    canvas.on("release", function(e) {
      return Tools[state.activeTool].release(e, state);
    });
    return canvas;
  };

  SimpleTool = function(touch) {
    return {
      touch: touch,
      move: function() {},
      release: function() {}
    };
  };

  Tools = {
    pan: (function() {
      var initialPan, startPos;
      startPos = null;
      initialPan = null;
      return {
        touch: function(e, _arg) {
          var viewport;
          viewport = _arg.viewport;
          startPos = e;
          return initialPan = {
            x: viewport.x,
            y: viewport.y
          };
        },
        move: function(_arg, _arg1) {
          var deltaX, deltaY, sX, sY, viewport, x, y;
          x = _arg.x, y = _arg.y;
          viewport = _arg1.viewport;
          if (startPos) {
            sX = startPos.x, sY = startPos.y;
            deltaX = (sX - x) * viewport.width;
            deltaY = (sY - y) * viewport.height;
            viewport.x = initialPan.x + deltaX;
            return viewport.y = initialPan.y + deltaY;
          }
        },
        release: function(e, _arg) {
          var viewport;
          viewport = _arg.viewport;
          console.log(e, viewport);
          return startPos = null;
        }
      };
    })(),
    inspect: SimpleTool(function(e, state) {
      return console.log(worldPosition(e, state.viewport));
    })
  };

  worldPosition = function(_arg, viewport) {
    var x, y;
    x = _arg.x, y = _arg.y;
    return {
