(function() {
  module.exports = function(_arg) {
    var data, height, length, self, width;
    width = _arg.width, height = _arg.height, data = _arg.data;
    length = width * height;
    if (data == null) {
      data = new Uint8Array(length);
    }
    return self = {
      region: function(rect, iterator) {
        var x, xPosition, xStart, y, yPosition, yStart;
        xStart = rect.x;
        yStart = rect.y;
        y = 0;
        while (y < rect.height) {
          x = 0;
          while (x < rect.width) {
            xPosition = x + xStart;
            yPosition = y + yStart;
            iterator(self.get(xPosition, yPosition), xPosition, yPosition);
            x += 1;
          }
          y += 1;
        }
        return self;
      },
      get: function(x, y) {
        if ((0 <= x && x < width) && (0 <= y && y < height)) {
          return data[y * width + x];
        }
      },
      set: function(x, y, value) {
        if ((0 <= x && x < width) && (0 <= y && y < height)) {
          return data[y * width + x] = value;
        } else {
          throw new Error("index out of bounds");
        }
      },
      data: data
    };
  };

}).call(this);

