main.content:
(function() {
  var Preload, Renderer, World, canvas, draw, renderer, state, step, update, view, world;

  require("./setup");

  World = require("./world");

  Renderer = require("./render");

  renderer = null;

  view = {
    x: 0,
    y: 0,
    width: 32,
    height: 18
  };

  global.view = view;

  world = World({
    width: 512,
    height: 512
  });

  state = {
    viewport: view,
    world: world,
    activeTool: "pan"
  };

  canvas = require("./canvas")(state);

  document.body.appendChild(canvas.element());

  update = function() {};

  draw = function() {
    return renderer != null ? renderer.draw(canvas, state) : void 0;
  };

  step = function() {
    update();
    draw();
    return requestAnimationFrame(step);
  };

  step();

  Preload = require("./preload");

  Promise.all(["Objects/Wall", "Objects/Floor", "Objects/Ground0", "Objects/Ground1", "Characters/Player0", "Characters/Player1", "Objects/Pit0", "Objects/Pit1"].map(Preload.image)).then(function(sheets) {
    var characters, tiles;
    tiles = [[1, 1, 19], [6, 1, 11], [1, 8, 7, true], [1, 15, 7, true], [1, 15, 19, true], [0, 3, 3], [4, 0, 0], [5, 0, 0]];
    characters = [0, 1, 2, 3, 4, 5, 6, 7].map(function(x) {
      return [4, x, 0];
    });
    return renderer = Renderer(sheets, tiles, characters);
  });

}).call(this);

