(function() {
  var Ajax, ajax, basePath;

  Ajax = require("ajax");

  ajax = Ajax();

  basePath = "https://danielx.whimsy.space/DawnLike/";

  module.exports = {
    image: function(name) {
      return ajax.getBlob("" + basePath + name + ".png?o_0").then(function(blob) {
        return new Promise(function(resolve, reject) {
          var img;
          img = new Image;
          img.onload = function() {
            return resolve(img);
          };
          img.onerror = reject;
          return img.src = URL.createObjectURL(blob);
        });
      });
    }
  };

}).call(this);

