(function() {
  var ByteGrid, rand;

  ByteGrid = require("./lib/byte-grid");

  rand = function(n) {
    return Math.floor(Math.random() * n);
  };

  module.exports = function(I) {
    var entities, grid, height, self, width, _i, _results;
    width = I.width, height = I.height;
    grid = ByteGrid({
      width: width,
      height: height
    });
    (function() {
      _results = [];
      for (_i = 0; _i < 128; _i++){ _results.push(_i); }
      return _results;
    }).apply(this).forEach(function(y) {
      var _i, _results;
      return (function() {
        _results = [];
        for (_i = 0; _i < 128; _i++){ _results.push(_i); }
        return _results;
      }).apply(this).forEach(function(x) {
        return grid.set(x, y, rand(3));
      });
    });
    entities = [0, 1, 2, 3, 4, 5, 6, 7].map(function(x) {
      return [x, 16 + x % 5, 13 + x % 3];
    });
    return self = {
      getTile: grid.get,
      region: grid.region,
      entities: function() {
        return entities;
      }
    };
  };

}).call(this);

