(function() {
  var Canvas;

  Canvas = require("../pixie_canvas");

  describe("pixie canvas", function() {
    return it("Should create a canvas", function() {
      var canvas;
      canvas = Canvas({
        width: 400,
        height: 150
      });
      assert(canvas);
      return assert(canvas.width() === 400);
    });
  });

}).call(this);

