(function() {
  var SimpleTool, Tools, TouchCanvas, worldPosition;

  TouchCanvas = require("touch-canvas");

  module.exports = function(game) {
    var canvas;
    canvas = TouchCanvas({
      width: 512,
      height: 288
    });
    canvas.on("touch", function(e) {
      return Tools[game.activeTool()].touch(e, game);
    });
    canvas.on("move", function(e) {
      return Tools[game.activeTool()].move(e, game);
    });
    canvas.on("release", function(e) {
      return Tools[game.activeTool()].release(e, game);
    });
    return canvas;
  };

  SimpleTool = function(touch) {
    return {
      touch: touch,
      move: function() {},
      release: function() {}
    };
  };

  Tools = {
    pan: (function() {
      var initialPan, startPos;
      startPos = null;
      initialPan = null;
      return {
        touch: function(e, game) {
          var viewport;
          viewport = game.viewport();
          startPos = e;
          return initialPan = {
            x: viewport.x,
            y: viewport.y
          };
        },
        move: function(_arg, game) {
          var deltaX, deltaY, sX, sY, viewport, x, y;
          x = _arg.x, y = _arg.y;
          viewport = game.viewport();
          if (startPos) {
            sX = startPos.x, sY = startPos.y;
            deltaX = (sX - x) * viewport.width;
            deltaY = (sY - y) * viewport.height;
            viewport.x = initialPan.x + deltaX;
            return viewport.y = initialPan.y + deltaY;
          }
        },
        release: function(e, game) {
          return startPos = null;
        }
      };
    })(),
    inspect: SimpleTool(function(e, game) {
      return console.log(worldPosition(e, game.viewport()));
    })
  };

  worldPosition = function(_arg, viewport) {
    var x, y;
    x = _arg.x, y = _arg.y;
    return {
      x: x * viewport.width,
      y: y * viewport.height
    };
  };

}).call(this);

