culture/name_generator.content:
(function() {
  var AngloSaxon, Cities, rand;

  AngloSaxon = require("./data/names/anglo-saxon");

  Cities = require("./data/cities/ca");

  rand = function(array) {
    var index;
    index = Math.floor(Math.random() * array.length);
    return array[index];
  };

  module.exports = function() {
    return {
      generate: function() {
        return rand(AngloSaxon.male)[0];
      },
      randomCity: function() {
        return rand(Cities);
      }
    };
  };

}).call(this);

game.content:
(function() {
  var Model, World;

  Model = require("model");

  World = require("./world");

  module.exports = function(I, self) {
    if (I == null) {
      I = {};
    }
    if (self == null) {
      self = Model(I);
    }
    if (I.step == null) {
      I.step = 0;
    }
    self.attrObservable("activeTool", "tools");
    self.attrAccessor("viewport");
    self.attrModel("world", World);
    self.extend({
      update: function() {
        if (I.step % 10 === 0) {
          self.world().entities().forEach(function(entity) {
            return entity.move();
          });
        }
        return I.step += 1;
      }
    });
    return self;
  };

}).call(this);

