module.exports = "* {
  box-sizing: border-box;
}
html {
  height: 100%;
}
body {
  background-color: #140c1c;
  color: #1b1421;
  font-family: "HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif;
  font-weight: 300;
  font-size: 18px;
  height: 100%;
  margin: 0;
  overflow: hidden;
  user-select: none;
}
h1,
h2,
h3 {
  margin: 0;
}
label {
  display: block;
}
label > h3 {
  display: inline-block;
  font-size: 100%;
  margin-right: 0.25em;
}
canvas {
  bottom: 0;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  margin: auto;
  width: 1536px;
  height: 864px;
  image-rendering: -moz-crisp-edges;
  image-rendering: -o-crisp-edges;
  image-rendering: -webkit-optimize-contrast;
  image-rendering: pixelated;
  -ms-interpolation-mode: nearest-neighbor;
}
.detail {
  position: absolute;
  right: 0;
  height: 100%;
  width: 300px;
  pointer-events: none;
}
card {
  border: 1px solid #1b1421;
  display: block;
  border-radius: 4px;
  padding: 8px;
  background-color: #deeed6;
  pointer-events: all;
}
.tools {
  position: absolute;
  top: 0;
  left: 0;
}
";

